<?phpglobal $post;$wstr_settings = get_post_meta($post->ID, "wstr_scroll_top_pages", true);if($wstr_settings === 'on'){    ?>    <input type="hidden" class="post_page_id" value="on">    <?php}else{    if(is_array($wstr_settings)){        foreach($wstr_settings as $roll_setting){            $page_post_id = $roll_setting;            ?>            <input type="hidden" class="post_page_id" value="<?php echo $page_post_id ?>">            <?php        }    }}?><h1>Choose pages and posts or select post and select page</h1><br><input type="checkbox" class="post_page" name="post_page"><span>all posts/pages</span><hr><p class="post_title">Select post</p><div class="selected_pages">    <?php    $args = array("posts_per_page" => -1);    $posts = get_posts($args);    foreach ($posts as $post) {        ?>        <div class="page-div">            <label>                <input type="checkbox" class='page_post post' name="page_id[]" value="<?php echo $post->ID; ?>">                <?php echo $post->post_title; ?>            </label>        </div>        <?php    }    ?></div><hr><p class="page_title">Select page</p><div class="selected_pages">    <?php    $posts = get_pages($args);    foreach ($posts as $post) {        ?>        <div class="page-div">            <label>                <input type="checkbox" class='page_post page' name="page_id[]" value="<?php echo $post->ID; ?>">                <?php echo $post->post_title; ?>            </label>        </div>        <?php    }    ?></div>