<?phpClass WSTR_register_posts{    protected static $instance = null;    private function __construct(){        add_action('init', array($this, 'wstr_register_posts'));    }    public function wstr_register_posts(){        $labels = array(            'name' => 'Scroll',            'singular_name' => 'Scroll',            'name_admin_bar' => 'Scroll',            'add_new' => 'Add New Scroll',            'add_new_item' => 'Add New Scroll',            'new_item' => 'New Scroll',            'edit_item' => 'Edit Scroll',            'view_item' => 'View Scroll',            'all_items' => 'All Scrolls',            'search_items' => 'Search Scroll',            'not_found' => 'No Scrolls found.',            'not_found_in_trash' => 'No scrolls found in Trash.',        );        $supports = array(            'title',        );        $args = array(            'labels'               => $labels,            'supports'             => $supports,            'public'               => true,            'capability_type'      => 'post',            'rewrite'              => array( 'slug' => 'roll' ),            'has_archive'          => true,            'menu_position'        => 40,            'menu_icon'            => 'dashicons-arrow-up'        );        register_post_type( 'wstr', $args);    }    public static function get_instance() {        if (null == self::$instance) {            self::$instance = new self;        }        return self::$instance;    }}